package day6.operators;

public class ArithmaticExample1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Variables Definition and Initialization
				int number1 = 13, number2 = 4;
				// Addition Operation
				int res = number1 + number2;
				System.out.println("Sum is: " + res);//
				// Subtraction Operation
				res = number1 - number2;//re-initialization of result variable
				System.out.println("Difference is : " + res);//
				// Multiplication Operation
				res = number1 * number2;
				System.out.println("Multiplied value is : " + res);//
				// Division Operation
				res = number1 / number2;//
				System.out.println("Quotient is : " + res);//
				// Modulus Operation
				res = number1 % number2;//
				System.out.println("Remainder is : " + res);//
			}
		}