package day8.forloopExamples;

public class Example11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=2;
		System.out.println("value of i="+i);
		++i;
		System.out.println("updated value of i="+i);
		i--;
		System.out.println("value of i="+i);
		i++;i++;i++;i--;i--;i++;
		System.out.println("value of i="+i);
		
	}

}
