package switchCaseExamples;

public class Example2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int month=1;
		switch(month) {
		case 1:
			System.out.println("Month is January");
			break;
		case 2:
			System.out.println("Month is February");
			break;
		}
	}

}
